import { escapeRooms } from '../data/escapeRooms';

export const getEscapeRoomsByName = ( name = '' ) => {

    if( name.length === 0 ){

        return [];
    }
    name = name.toLocaleLowerCase();

    return escapeRooms.filter(escapeRoom => escapeRoom.titulo.toLowerCase().includes(name));

}
